#include "CSTimeWindow.hpp"
#include "../include/CrossSection.hpp"

CSTimeWindow::CSTimeWindow(int64_t startTime_, int64_t endTime_) {
    startTime = startTime_;
    endTime = endTime_;
}

void CSTimeWindow::process(CrossSection *item) {
    if (startTime <= item->getTime() && item->getTime() <= endTime) {
        callNext(item);
    } else {
        delete item;
    }
}
